import React, { useState } from "react";
import axios from "axios";
import '../App.css';

const CreateRecipe = (props) => {
    const [name, setName] = useState("");
    const [creator, setCreator] = useState("");
    const [category, setCategory] = useState("");
    const [time, setTime] = useState("");
    const [calories, setCalories] = useState("");
    const [ingredient, setIngredient] = useState("");
    // const [measurement, setMeasurement] = useState("");
    const [instructions, setInstructions] = useState("");
    // const [ingredientList, setIngredientList] = useState([]);

    const submitHandler = (e) => {
        e.preventDefault();

        axios.post("http://localhost:8000/api/reshappyDB", {
            name,
            creator,
            category,
            time,
            calories,
            ingredient,
            // measurement,
            instructions,
            // ingredient_list: ingredientList
        })
            .then((res) => {
                console.log(res.data);
                setName("");
                setCreator("");
                setCategory("");
                setTime("");
                setCalories("");
                setIngredient("");
                // setMeasurement("");
                setInstructions("");
            })
            .catch((err) => { console.log(err); })
    }

    // const addIngredient = () => {
    //     setIngredientList([...ingredientList, {ingredient, measurement}]);
    //     setIngredient("");
    //     setMeasurement("");
    // }

    return (
        <div>
            <Link to={`/`}>Back</Link>
            <h1>Create Recipe</h1>
            <form onSubmit={submitHandler}>
                <div className="form-fields">
                    <label>Name: </label>
                    <input onChange={(e) => setName(e.target.value)} value={name} name="name" type="text" />
                </div>
                <br/>
                <div className="form-fields">
                    <label>Creator: </label>
                    <input onChange={(e) => setCreator(e.target.value)} value={creator} name="creator" type="text" />
                </div>
                <br/>
                <div className="form-fields">
                    <label>Category: </label>
                    <input onChange={(e) => setCategory(e.target.value)} value={category} name="category" type="text" />
                </div>
                <br/>
                <div className="form-fields">
                    <label>Time: </label>
                    <input onChange={(e) => setTime(e.target.value)} value={time} name="time" type="text" />
                </div>
                <br/>
                <div className="form-fields">
                    <label>Calories: </label>
                    <input onChange={(e) => setCalories(e.target.value)} value={calories} name="calories" type="text" />
                </div>
                <br/>
                <div className="form-fields">
                    <label>Ingredients with Measurment: </label> 
                    <input onChange={(e) => setIngredient(e.target.value)} value={ingredient} name="ingredient" type="text"/> 
                    <p/>
                    {/* <label>Measurement: </label>
                    <input onChange={(e) => setMeasurement(e.target.value)} value={measurement} name="measurement" type="text" />
                    <br/> */}
                    {/* <button type="button" onClick={addIngredient}>Add</button> */}
                </div>
                
                {/* {ingredientList.map((item, index) => {
                    console.log(ingredientList);
                    // console.log(item);
                    // console.log(ingredientList.length);
                    return (
                        <div key={index}>{`${item.measurement} ${item.ingredient}`}</div>
                    );
                })} */}
                <br/>
                <div className="form-fields">
                    <label>Instructions: </label>
                    <input onChange={(e) => setInstructions(e.target.value)} value={instructions} name="instructions" type="text" />
                </div>
                <br/>
                <input type="submit" value="create recipe"></input>
            </form>
        </div>
    );
};

export default CreateRecipe;


//TO-DO: Add a drop-down menu for choosing Categories, post array of ingredients to the database, edit the edit_recipe page to reflect that
