import React, { useState, useEffect } from "react";
import {useNavigate, useParams} from 'react-router-dom';
import axios from "axios";

const GetOne = (props) => {
    const { id } = useParams();
    const [one_product, setOne_product] = useState({});

    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:8000/api/reshappyDB/${id}`)
            .then((res) => { setOne_product(res.data); })
            .catch((err) => { console.log(err); });
    }, []);

    const deleteHandler = () => {
        axios.delete(`http://localhost:8000/api/reshappyDB/${id}`)
            .then((res) => {navigate("/");})
            .catch((err) => {console.log(err);});
    };

    return (
        <div>
            <h2>{one_product.title}</h2>
            <p>Price: ${one_product.price}</p>
            <p>Description: {one_product.description}</p>
            <button onClick={deleteHandler}>delete</button>
            <br/>
            <button onClick={() => navigate(`/product/edit/${id}`)}>edit</button>
        </div>
    );
};

export default GetOne;
