import React, { useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import '../App.css';


const Homepage = (props) => {
    
    const { recipeList, setRecipeList} = props;

    useEffect(() => {
        axios.get(`http://localhost:8000/api/reshappyDB/${id}`)
            .then((res) => { setRecipeList(res.data); })
            .catch((err) => console.log(err));
    }, [setRecipeList]);

    const randomRecipe = e => {
        var item = recipeList[Math.floor(Math.random()*recipeList.length)];
    };

    return (
        <div className="container-1">
            <div className="banner">
                <div className="text-1">
                    <h1 style={{color:'white', fontSize:'70px', margin:'0px'}}>Res-happy</h1>
                    <h1 style={{color:'white', margin:'0px'}}>Let's get cooking!</h1>
                    <br/>
                    <h3 style={{ marginTop:'80px'}}>The place to store all your happy recipes!</h3>
                    
                </div>
            </div>
            <div className="RecBox">
                <div className="newRec">
                    <h1>Already have 
                    <br/>
                    a Recipe?</h1>
                    <Link to={`/recipe/new`}>
                        <button>Add it!</button>
                    </Link>
                </div>
                <div className="allRec">
                    <h1>Looking for
                    <br/>
                    a Recipe?</h1>
                    <Link to={`/recipe/new`}>
                        <button>Come Take a Look!</button>
                    </Link>
                </div>
                
                { recipeList.map((recipe, index) => (
                <div key={index}>
                    <Link to={recipe._id}>look</Link>
                </div>
            ))}
            </div>

        </div>
    );
}

export default Homepage;
