import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const Edit = (props) => {
    const {id} = useParams();
    const [name, setName] = useState("");
    const [creator, setCreator] = useState("");
    const [category, setCategory] = useState("");
    const [time, setTime] = useState("");
    const [calories, setCalories] = useState("");
    const [ingredient, setIngredient] = useState("");
    const [measurement, setMeasurement] = useState("");
    const [instructions, setInstructions] = useState("");

    const navigate = useNavigate();

    useEffect(() => {
        axios.get("http://localhost:8000/api/reshappyDB/${id}")
            .then((res) => {
                setName(res.data.name);
                setCreator(res.data.creator);
                setCategory(res.data.category);
                setTime(res.data.time);
                setCalories(res.data.calories);
                setIngredient(res.data.ingredient);
                setMeasurement(res.data.measurement);
                setInstructions(res.data.instructions);
            })
            .catch((err) => console.log(err));
    }, []);

    const submitHandler = (e) => {
        e.preventDefault();

        axios.put(`http://localhost:8000/api/reshappyDB/${id}`, {
            name: name,
            creator: creator,
            category: category,
            time: time,
            calories: calories,
            ingredient: ingredient,
            measurement: measurement,
            instructions: instructions
        })
            .then((res) => {navigate("/");})
            .catch((err) => {console.log(err);});
    };

    return (
        <div>
            <h1>Edit Product</h1>
            <form onSubmit={submitHandler}>
                <div className="form-fields">
                    <label>Name: </label>
                    <input onChange={(e) => setName(e.target.value)} value={name} name="title" type="text"/>
                </div>
                <br/>
                {/* <input class="submit-input" type="submit" value="update" /> */}
            </form>
        </div>
    );

};

export default Edit;
