import React, { useState, useEffect } from "react";
import {useNavigate, Link, useParams} from 'react-router-dom';
import axios from "axios";
import '../App.css';

const GetOne = (props) => {
    const { id } = useParams();
    const [oneRecipe, setOneRecipe] = useState({});

    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:8000/api/reshappyDB/${id}`)
            .then((res) => { setOneRecipe(res.data); })
            .catch((err) => { console.log(err); });
    }, [id]);

    const deleteHandler = () => {
        axios.delete(`http://localhost:8000/api/reshappyDB/${id}`)
            .then((res) => {navigate(`/recipe/all`);})
            .catch((err) => {console.log(err);});
    };

    return (
        <div className="showRec">
            <div className="Banner-2">
                <div className="text-2">
                    <h1 style={{color:'white', margin:'0px'}}>Recipe Page</h1>
                    <Link to={`/`} style={{paddingTop:'4px'}}>
                        <button>Homepage</button>
                    </Link>
                </div>
            </div>

            <div>
                <h1>{oneRecipe.name}</h1>
                <Link to={`/recipe/all`}>
                    <button>Back</button>
                </Link>
            </div>
            <p>Creator: {oneRecipe.creator}</p>
            <p>Category: {oneRecipe.category}</p>
            <p>Time: {oneRecipe.time}</p>
            <p>Calories: {oneRecipe.calories}</p>
            <p>Ingredients: {oneRecipe.ingredient}</p>
            {/* <p>Measurement: {oneRecipe.measurement}</p> */}
            <p>Instructions: {oneRecipe.instructions}</p>
            <br/>
            <button onClick={() => navigate(`/recipe/edit/${id}`)}>Edit Recipe</button>
            <button onClick={deleteHandler} style={{backgroundColor:'red'}}>Delete Recipe</button>
        </div>
    );
};

export default GetOne;
