import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const GetAll = (props) => {
    const [recipeList, setRecipeList] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/reshappyDB`)
            .then((res) => { setRecipeList(res.data); })
            .catch((err) => console.log(err));
    }, []);

    return (
        <div className="allRecContainer">
            <div>
                <h1>All Recipes</h1>
                <Link to={`/`}>
                    <button>Back</button>
                </Link>
            </div>
            <div className="allRecipes">
                { recipeList.map((recipe, index) => (
                    <div key={index}>
                        <Link to={`/recipe/${recipe._id}`} style={{textDecoration:'none'}}><h2>{recipe.name}, created by {recipe.creator}</h2></Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default GetAll;
