import React, { useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const GetAll = (props) => {
    const { recipeList, setRecipeList} = props;

    useEffect(() => {
        axios.get("http://localhost:8000/api/products")
            .then((res) => { setRecipeList(res.data); })
            .catch((err) => console.log(err));
    }, []);

    const delete_recipe = (recipe_id) => {

        axios.delete(`http://localhost:8000/api/reshappyDB/${recipe_id}`)
            .then((res) => {setRecipeList(recipeList.filter((recipe, index) => recipe._id !== recipe_id))})
            .catch((err) => {
                console.log(err);
            });
    };

    return (
        <div>
            <h1>All Products:</h1>
            { recipeList.map((product, index) => (
                <div key={index}>
                    <Link to={`/product/${product._id}`}>{product.title}</Link>
                    <button onClick={(e)=>{delete_product(product._id)}}>delete</button>
                </div>
            ))}
        </div>
    );
};

export default GetAll;
