import React, { useState, useEffect } from "react";
import {useNavigate, useParams} from 'react-router-dom';
import axios from "axios";

const GetOne = (props) => {
    const { id } = useParams();
    const [oneRecipe, setOneRecipe] = useState({});

    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:8000/api/reshappyDB/${id}`)
            .then((res) => { setOneRecipe(res.data); })
            .catch((err) => { console.log(err); });
    }, [id]);

    const deleteHandler = () => {
        axios.delete(`http://localhost:8000/api/reshappyDB/${id}`)
            .then((res) => {navigate("/");})
            .catch((err) => {console.log(err);});
    };

    return (
        <div>
            <h2>{oneRecipe.name}</h2>
            <p>Creator: {oneRecipe.creator}</p>
            <p>Category: {oneRecipe.category}</p>
            <p>Time: {oneRecipe.time}</p>
            <p>
                Calories:
                <select name="category" id="selectList">
                    <option value="option 1">Option 1</option>
                    <option value="option 2">Option 2</option>
                </select>
            </p>
            <p>Ingredient: {oneRecipe.ingredient}</p>
            <p>Measurement: {oneRecipe.measurement}</p>
            <p>Instructions: {oneRecipe.instructions}</p>
            <button onClick={deleteHandler}>delete recipe</button>
            <br/>
            <button onClick={() => navigate(`/recipe/edit/${id}`)}>edit recipe</button>
        </div>
    );
};

export default GetOne;
